import React from "react";

const FilterForm = ({ handleFilterList, searchTitle }) => {
  document.getElementById("searchFrom").onkeypress = (e) => {
    // form1に入力されたキーを取得
    const key = e.keyCode || e.charCode || 0;
    // 13はEnterキーのキーコード
    if (key == 13) {
      // アクションを行わない
      e.preventDefault();
    }
  };
  return (
    <div className="mt-3">
      <span>{searchTitle}</span>
      <form className="d-inline" id="searchForm">
        <input
          type="text"
          //   placeholder="名前or顧客IDを入力"
          onChange={handleFilterList}
        />
      </form>
    </div>
  );
};

export default FilterForm;
