import React, { useState } from "react";

export default function FrameForm({ frameAdded }) {
  const [frameName, setFrameName] = useState("");
  const [productNumber, setProductNumber] = useState("");
  const [price, setPrice] = useState("");
  const [priceTax, setPriceTax] = useState("");
  const [frameType, setFrameType] = useState("");
  const [series, setSeries] = useState("");
  const [category, setCategory] = useState("");
  const [description, setDescription] = useState("");
  const [keywords, setKeywords] = useState("");
  const [target, setTarget] = useState("");
  const [size, setSize] = useState("");
  const [weight, setWeight] = useState("");
  const [nosePad, setNosePad] = useState("");
  const [materialFront, setMaterialFront] = useState("");
  const [materialTemple, setMaterialTemple] = useState("");
  const [color, setColor] = useState("");

  const resetFrameForm = () => {
    setFrameName("");
    setProductNumber("");
    setPrice("");
    setPriceTax("");
    setFrameType("");
    setSeries("");
    setCategory("");
    setDescription("");
    setKeywords("");
    setTarget("");
    setSize("");
    setWeight("");
    setNosePad("");
    setMaterialFront("");
    setMaterialTemple("");
    setColor("");
  };

  const submitFrame = async (e) => {
    e.preventDefault();
    try {
      await fetch("/api/frames", {
        method: "POST",
        body: JSON.stringify({
          frameName,
          productNumber,
          price,
          priceTax,
          frameType,
          series,
          category,
          description,
          keywords,
          target,
          size,
          weight,
          nosePad,
          materialFront,
          materialTemple,
          color,
        }),
      });
      resetFrameForm();
      frameAdded();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="card">
      <div className="card-header">フレーム情報を追加する</div>
      <div className="card-body">
        <form className="" onSubmit={submitFrame}>
          <div className="form-group">
            <label htmlFor="frameName">品名</label>
            <input
              type="text"
              name="frameName"
              value={frameName}
              className="form-control"
              onChange={(e) => setFrameName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="productNumber">品番</label>
            <input
              type="text"
              name="productNumber"
              value={productNumber}
              className="form-control"
              onChange={(e) => setProductNumber(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="price">価格（税抜）</label>
            <input
              type="number"
              name="price"
              value={price}
              className="form-control"
              onChange={(e) => setPrice(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="priceTax">価格（税込）</label>
            <input
              type="number"
              name="priceTax"
              value={priceTax}
              className="form-control"
              onChange={(e) => setPriceTax(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="frameType">フレームタイプ</label>
            <input
              type="text"
              name="frameType"
              value={frameType}
              className="form-control"
              onChange={(e) => setFrameType(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="series">シリーズ</label>
            <input
              type="text"
              name="series"
              value={series}
              className="form-control"
              onChange={(e) => setSeries(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="cateory">カテゴリ</label>
            <input
              type="text"
              name="category"
              value={category}
              className="form-control"
              onChange={(e) => setCategory(e.target.value)}
            />
          </div>
          <button type="submit" className="btn btn-primary">
            追加する
          </button>
        </form>
      </div>
    </div>
  );
}
