import React, { useState } from "react";

export default function SortButton({
  handleSortByAscend,
  handleSortByDescend,
}) {
  const sortByAscend = (e) => {
    e.preventDefault();
    this.props.handleSortByAscend(e.target.value);
  };
  const sortByDescend = (e) => {
    e.preventDefault();
    this.props.handleSortByDescend(e.target.value);
  };

  return 0;
}
