import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { UserProfileAvatar } from "../components/user/UserProfileAvatar";
import RecordList from "../components/RecordList";
import RecordForm from "../components/RecordForm";

const ClinicRecords = () => {
  const { user } = useAuth0();

  const [records, setCourses] = useState([]);
  const loadRecords = async () => {
    try {
      const res = await fetch("/api/records");
      const records = await res.json();
      setCourses(records);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    loadRecords();
  }, []);

  return (
    <div>
      {/* <h1>Profile</h1>
      <UserProfileAvatar style={{ width: "50px" }} />
      <h2>name</h2>
      <div>{user.name}</div>
      <h2>nickname</h2>
      <div>{user.nickname}</div>
      <h2>picture</h2>
      <div>{user.picture}</div>
      <h2>email</h2>
      <div>{user.email}</div> */}
      <div className="container mt-5 mb-5">
        <h1 className="mb-5 text-center">カルテ編集</h1>
        <RecordForm recordAdded={loadRecords} />
        <RecordList records={records} refreshRecords={loadRecords} />
      </div>
    </div>
  );
};

export default ClinicRecords;
