import React, { useState, useEffect } from "react";
import Chart from "./Chart";
import SortButton from "../functions/SortButton";

export default function ChartList({ charts, refreshCharts }) {
  const [sortList, setSortList] = useState(charts);
  const [filterList, setFilterList] = useState(charts);

  useEffect(() => {
    const sorted = [...charts].sort((a, b) => {
      if (a.sort_id > b.sort_id) {
        return -1;
      } else {
        return 1;
      }
    });
    setSortList(sorted);
    setFilterList(charts);
  }, [charts]);

  const handleSortAscend = () => {
    const sorted = [...charts].sort((a, b) => {
      if (a.sort_id < b.sort_id) {
        return -1;
      } else {
        return 1;
      }
    });
    setSortList(sorted);
  };

  const handleSortDsecend = () => {
    const sorted = [...charts].sort((a, b) => {
      if (a.sort_id > b.sort_id) {
        return -1;
      } else {
        return 1;
      }
    });
    setSortList(sorted);
  };

  const handleFilterList = (e) => {
    const updateList = [...charts].filter((item) => {
      return item.serch(e.target.value) !== -1;
    });
    setSortList(sorted);
  };

  return (
    <div>
      <h2 className="mt-5 mb-3">カルテ一覧</h2>
      <SortButton
        handleSortDescend={handleSortDsecend}
        handleSortAscend={handleSortAscend}
      />
      <div className="list-group">
        {sortList.map((chart) => (
          <Chart chart={chart} key={chart.id} refreshCharts={refreshCharts} />
        ))}
      </div>
    </div>
  );
}
