  return (
    <div>
      <div>
        <span style={{ marginRight: "8px", fontSize: "12px" }}>
          登録順で並べ替え:
        </span>
        {/* idで昇順にソートするボタン（valueにidを設定） */}
        <button onClick={handleSortByAscend(charts)} value="id">
          昇順
        </button>
        {/* idで降順にソートするボタン（valueにidを設定） */}
        <button onClick={handleSortByDescend(charts)} value="id">
          降順
        </button>
      </div>
    </div>
  );
}
