import React from "react";

export default function Course({ record, refreshRecords }) {
  const markRecordPurchased = async () => {
    try {
      await fetch("/api/records", {
        method: "PUT",
        body: JSON.stringify({ ...record, purchased: true }),
      });
      refreshRecords();
    } catch (err) {
      console.error(err);
    }
  };

  const deleteRecord = async () => {
    try {
      await fetch("/api/records", {
        method: "DELETE",
        body: JSON.stringify({ id: record.id }),
      });
      refreshRecords();
    } catch (err) {
      console.error(err);
    }
  };
  return (
    <div className="list-group-item">
      <a href={record.link}>
        <h4 className="list-group-item-heading">{record.name}</h4>
      </a>
      {/* <p>
        Tags:{" "}
        {record.tags &&
          record.tags.map((tag, index) => (
            <span className="badge badge-primary mr-2" key={index}>
              {tag}
            </span>
          ))}
      </p> */}
      {/* {!record.purchased && (
        <button
          className="btn btn-sm btn-primary"
          onClick={markRecordPurchased}
        >
          購入した
        </button>
      )} */}
      <button className="btn btn-sm btn-danger ml-2" onClick={deleteRecord}>
        削除
      </button>
    </div>
  );
}
