import React from "react";
import Chart from "./Chart";

export default function ChartList({ charts, refreshCharts }) {
  const handleSortByAscend = (key) => {
    const line = charts.sort((a, b) => {
      if (a[key] < b[key]) return -1;
      if (a[key] > b[key]) return 1;
      return 0;
    });
    this.setState({
      charts: line,
    });
  };
  const handleSortByDescend = (key) => {
    const line = charts.sort((a, b) => {
      if (a[key] < b[key]) return 1;
      if (a[key] > b[key]) return -1;
      return 0;
    });
    this.setState({
      charts: line,
    });
  };
  console.log(charts);
  return (
    <div>
      <h2 className="mt-5 mb-3">カルテ一覧</h2>
      <div className="list-group">
        {charts.map((chart) => (
          <Chart chart={chart} key={chart.id} refreshCharts={refreshCharts} />
        ))}
      </div>
    </div>
  );
}
