import React, { useState } from "react";

const SortButton = ({ handleSortByAscend, handleSortByDescend }) => {
  const sortByAscend = (e) => {
    e.preventDefault();
    handleSortByAscend(e.target.value);
  };
  const sortByDescend = (e) => {
    e.preventDefault();
    handleSortByDescend(e.target.value);
  };

  return (
    <div>
      <div>
        <span style={{ marginRight: "8px", fontSize: "12px" }}>
          登録順で並べ替え:
        </span>
        {/* idで昇順にソートするボタン（valueにidを設定） */}
        <button onClick={sortByAscend} value="id">
          昇順
        </button>
        {/* idで降順にソートするボタン（valueにidを設定） */}
        <button onClick={sortByDescend} value="id">
          降順
        </button>
      </div>
    </div>
  );
};

export default SortButton;
