import React from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { UserProfileAvatar } from "../components/user/UserProfileAvatar";

const AccountInfo = () => {
  const { user } = useAuth0();

  return (
    <div className="card mt-5">
      <h1 className="card-header mb-5 text-center">アカウント情報</h1>
      <div className>
        <div className="mb-5">
          <UserProfileAvatar
            style={{ width: "50px" }}
            className="text-center"
          />
          <h2>名前</h2>
          <div>{user.name}</div>
        </div>

        <div className="mb-5">
          <h2>ニックネーム</h2>
          <div>{user.nickname}</div>
        </div>
        {/* <h2>picture</h2>
      <div>{user.picture}</div> */}
        <div className="mb-5">
          <h2>メールアドレス</h2>
          <div>{user.email}</div>
        </div>
      </div>
    </div>
  );
};

export default AccountInfo;
