  charts.sort((a, b) => {
    if (a.customer_number < b.customer_number) {
      return -1;
    } else {
      return 1;
    }
  });
  console.log(charts);

  const handleSort = (key) => {
    console.log(key);
  };
  return (
    <div>
      <h2 className="mt-5 mb-3">カルテ一覧</h2>
      {/* <SortButton button={SORT_NUM} handleSort={handleSort} /> */}
      <div className="list-group">
        {charts.map((chart) => (
          <Chart chart={chart} key={chart.id} refreshCharts={refreshCharts} />
        ))}
      </div>
    </div>
  );
}
