import React, { useState, useEffect } from "react";
import Chart from "./Chart";
import SortButton from "../functions/SortButton";

export default function ChartList({ charts, refreshCharts }) {
  const [sort, setSort] = useState({});

  useEffect(() => {
    setSort(charts);
  });
  console.log(sort);
  const handleSortAscend = () => {
    charts.sort((a, b) => {
      if (a.customer_number < b.customer_number) {
        return -1;
      } else {
        return 1;
      }
    });
    setSort(`初期状態の配列：${charts}`);
    // console.log(sort);
  };

  const handleSortDsecend = () => {
    charts.sort((a, b) => {
      if (a.customer_number > b.customer_number) {
        return -1;
      } else {
        return 1;
      }
    });
    setSort(charts);
    // console.log(sort);
  };

  // const handleSort = (key) => {
  //   console.log(key);
  // };
  return (
    <div>
      <h2 className="mt-5 mb-3">カルテ一覧</h2>
      <SortButton
        handleSortDescend={handleSortDsecend}
        handleSortAscend={handleSortAscend}
      />
      <div className="list-group">
        {charts.map((chart) => (
          <Chart chart={chart} key={chart.id} refreshCharts={refreshCharts} />
        ))}
      </div>
    </div>
  );
}
