import React, { useState } from "react";

const SortButton = ({ button, handleSort }) => {
  return (
    <div>
      <div>
        <span style={{ marginRight: "8px", fontSize: "12px" }}>並べ替え:</span>
        {/* idで昇順にソートするボタン（valueにidを設定） */}
        <button onClick={() => handleSort(button)}>並び替え</button>
        {/* idで降順にソートするボタン（valueにidを設定） */}
      </div>
    </div>
  );
};

export default SortButton;
