const loader = document.querySelector("#loading");

const displayLoading = () => {
  loader.classList.add("display");
  setTimeout(() => {
    loader.classList.remove("display");
  }, 5000);
};

const hideLoading = () => {
  loader.classList.remove("display");
};

module.exports = { loader, displayLoading, hideLoading };
